
/*MDULE NOTE
a single function that takes three arguments: the directory name,
the filename extension string and a callback function, in that order.*/

var fs = require('fs');
var path = require('path');

var dir = process.argv[2];
var ext = process.argv[3];

var sortLs = function(dir, ext, readDir){
              fs.readdir(dir, function (err, list){
      if (err)
        return console.log('error')

    var extens = '.' + ext;

    var i = 0;
    while (i <= list.length)
    {
      if (extens == path.extname(list[i]))
        console.log(list[i]);
      i++;
    }
})
}

sortLs();

exports.sortLs = sortLs; /*
exports.readDir() = readDir();*/
